          <span>            {{ $t('page.selectCategory') }}          </span>        </div>        <div v-if="showSelectText && !categories.length" class="select-item">          <img src="@/assets/icons/area-bg.svg" alt="">          <span>            {{ $t('page.createCategory') }}          </span>        </div>        <router-view :closeDetails="closeDetails" name="helper" class="category-wrap"/>      </simplebar>      <simplebar force-visible>        <CategoryCreate :categories="categories" class="dialog-category"  ref="createCategory" />      </simplebar>    </div>  </div></template><script>import CategoriesList from "@/components/dashboard/categories/CategoriesList"import CategoryCreate from "@/components/dashboard/categories/CategoryCreate"export default {  props: {    value: {      required: false    }  },  name: "Categories",  data: () => ({    search: '',    create: false,    switchNesting: true,    showSelectText: true,    loading: true,    list: []  }),  components: {    CategoriesList,    CategoryCreate  },  computed: {    categories () {      return this.$store.getters['categories']    }  },  methods: {    createCategory() {      this.$refs.createCategory.open(this.categories)    },    closeDetails() {      this.create = false      this.showSelectText = !this.showSelectText    }  },  async mounted() {    await this.$store.dispatch('fetchCategories')    this.loading = false  }}</script><style lang="sass" scoped>.page .list-group li  flex-wrap: wrap  height: auto  display: block</style>